import { use, useEffect, useState } from 'react'
// import './App.css'
import axios from 'axios'


const App = () => {



  const [persons, setPersons] = useState([]) 

  useEffect(() => {
    console.log('effect')
    axios
      .get('http://localhost:3001/persons')
      .then(response => {
        console.log('promise fulfilled')
        setPersons(response.data)
      })
  }, [])
  console.log('render', persons.length, 'notes')


  const ContactList = ({contacts}) => {
    return(
      <ul>
        {contacts.map((contact,i)=>
        <li key={i} className='contacts'>
          Name : {contact.name},
          Mobile : {contact.number}
          <button onClick={()=>deletecontact(contact.id)}>Delete</button>
        </li>)}
      </ul>
    )
  }

  const deletecontact =(id) => {
     if (window.confirm("Are you sure you want to delete this contact?")) {
      axios
        .delete(`http://localhost:3001/persons/${id}`)
        .then(()=>{setPersons(persons.filter(person=> person.id!==id))})}
  }


  const [newPerson, setnewPerson] = useState('')
  const [newNum, setnewNum] = useState('')
  const [findName, setfindName]= useState('')

  const handleInputName=(event)=>{
    setnewPerson(event.target.value)
  }
  const handleInputNum=(event)=>{
    setnewNum(event.target.value)
  }
  const handlefindName=(event)=>{
    setfindName(event.target.value)
  }

  const addContacts = (event) =>{
    event.preventDefault()
    const nameExist = persons.some(person=>person.name.toLowerCase()===newPerson.toLowerCase())
    if (newPerson.trim()===''){
      alert(`Please enter name.`)
      return
    }
    if (nameExist) {
      if (window.confirm(`${newPerson} already exists in the phonebook. Do you want to replace the old number with the new one?`)) {
      const personToUpdate = persons.find(person => newPerson.toLowerCase() === person.name.toLowerCase())

      if (personToUpdate) {
        const updatedPerson = { ...personToUpdate, number: newNum };

        axios
          .put(`http://localhost:3001/persons/${personToUpdate.id}`, updatedPerson)
          .then(response => {
            setPersons(persons.map(person =>
              person.id === personToUpdate.id ? response.data : person
            ))
          })

    }}}
    const numExist = persons.some(person=>person.num===newNum)
    if (newNum.trim()===''){
      alert(`Please enter number.`)
      return
    }
    if (numExist) {
      alert(`${newNum}, Already Exist `)
      return
    }
    const newObj = {
      name : newPerson,
      number : newNum,
    }
    axios
      .post('http://localhost:3001/persons', newObj)
      .then(response =>{ 
        setPersons(persons.concat(response.data))
        setnewPerson('')
        setnewNum('')
        console.log(response.status)
        if (response.status===201){
          return <div className='Done Status'>Contact added.</div>
        }
      })
  }

  const filteredPersons = persons.filter(person =>
    person.name.toLowerCase().includes(findName.toLowerCase()))

  // findName.trim() ? console.log('True') : console.log('False')




  return(
    <>
      <h2>Phonebook</h2>
      <div className='searchBoxContainer'> 
      <input  value={findName} onChange={handlefindName} placeholder="Search"/>
      </div>

      <form onSubmit={addContacts}>
        <input  value={newPerson} onChange={handleInputName} placeholder='Name'/>
        <input value={newNum} onChange={handleInputNum} placeholder='Mobile Number'/>


        <button>Add to Contact</button>
      </form>
      <h3>My Contacts</h3>
      <ContactList contacts={findName.trim()? filteredPersons:persons} />
    </>
  )



}

export default App
